#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\jayde\luminis\node_modules\.pnpm\parse-domain@8.2.2\node_modules\parse-domain\bin\node_modules;C:\Users\jayde\luminis\node_modules\.pnpm\parse-domain@8.2.2\node_modules\parse-domain\node_modules;C:\Users\jayde\luminis\node_modules\.pnpm\parse-domain@8.2.2\node_modules;C:\Users\jayde\luminis\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/jayde/luminis/node_modules/.pnpm/parse-domain@8.2.2/node_modules/parse-domain/bin/node_modules:/mnt/c/Users/jayde/luminis/node_modules/.pnpm/parse-domain@8.2.2/node_modules/parse-domain/node_modules:/mnt/c/Users/jayde/luminis/node_modules/.pnpm/parse-domain@8.2.2/node_modules:/mnt/c/Users/jayde/luminis/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../parse-domain/bin/update.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../parse-domain/bin/update.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../parse-domain/bin/update.js" $args
  } else {
    & "node$exe"  "$basedir/../parse-domain/bin/update.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
